mapper.select:
SELECT COUNT(*)
FROM
    default_schedule
WHERE
    user_id = #{userId}
AND end_date >= #{startDate}
AND start_date <= #{endDate}

    AND id != #{id}

SELECT
    days_of_week AS daysOfWeek
FROM
    regular_schedule
WHERE
    user_id = #{userId}
AND end_date >= #{startDate}
AND start_date <= #{endDate}

    AND id != #{id}

SELECT COUNT(*)
FROM
    irregular_schedule
WHERE
    user_id = #{userId}
AND date >= #{startDate}
AND date <= #{endDate}

    AND id != #{id}
